import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
// Use local auth for development, Replit auth for production
const authModule = process.env.NODE_ENV === 'development' 
  ? await import("./localAuth")
  : await import("./replitAuth");

const { setupAuth, isAuthenticated } = authModule;
import { ObjectStorageService, ObjectNotFoundError } from "./objectStorage";
import { ObjectPermission } from "./objectAcl";
import {
  insertServiceSchema,
  insertCaseStudySchema,
  insertTeamMemberSchema,
  insertTestimonialSchema,
  insertClientSchema,
  insertTechnologySchema,
  insertCategorySchema,
  insertContactSubmissionSchema,
  insertSiteSettingSchema,
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Direct Admin Login Route (bypassed in development mode)
  app.post('/api/admin/login', async (req, res) => {
    try {
      // In development mode, automatically login as admin
      if (process.env.NODE_ENV === 'development') {
        const mockAdminUser = {
          id: 'local-admin-user',
          email: 'admin@pitetris.local',
          firstName: 'Local',
          lastName: 'Admin',
          isAdmin: true,
          loginType: 'direct'
        };

        // Set session for direct login
        (req.session as any).directAdminUser = mockAdminUser;

        return res.json({ 
          message: 'Login successful (development mode)', 
          user: mockAdminUser
        });
      }

      // Production logic would go here
      return res.status(501).json({ message: 'Admin login not implemented for production' });
    } catch (error) {
      console.error('Direct admin login error:', error);
      res.status(500).json({ message: 'Internal server error' });
    }
  });

  // Auth routes  
  app.get('/api/auth/user', async (req: any, res) => {
    try {
      // Check for direct admin login first
      if ((req.session as any).directAdminUser) {
        return res.json((req.session as any).directAdminUser);
      }

      // Then check Replit Auth
      if (!req.isAuthenticated() || !req.user?.claims?.sub) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Logout route for both direct and Replit auth
  app.post('/api/auth/logout', (req, res) => {
    // Clear direct admin session
    if ((req.session as any).directAdminUser) {
      delete (req.session as any).directAdminUser;
      return res.json({ message: 'Logged out successfully' });
    }

    // Handle Replit auth logout
    req.logout(() => {
      res.json({ message: 'Logged out successfully' });
    });
  });

  // Public API routes

  // Get all services
  app.get('/api/services', async (req, res) => {
    try {
      const services = await storage.getServices();
      res.json(services);
    } catch (error) {
      console.error("Error fetching services:", error);
      res.status(500).json({ message: "Failed to fetch services" });
    }
  });

  // Get service by slug
  app.get('/api/services/:slug', async (req, res) => {
    try {
      const service = await storage.getServiceBySlug(req.params.slug);
      if (!service) {
        return res.status(404).json({ message: "Service not found" });
      }
      res.json(service);
    } catch (error) {
      console.error("Error fetching service:", error);
      res.status(500).json({ message: "Failed to fetch service" });
    }
  });

  // Get all case studies
  app.get('/api/case-studies', async (req, res) => {
    try {
      const caseStudies = await storage.getCaseStudies();
      res.json(caseStudies);
    } catch (error) {
      console.error("Error fetching case studies:", error);
      res.status(500).json({ message: "Failed to fetch case studies" });
    }
  });

  // Get featured case studies
  app.get('/api/case-studies/featured', async (req, res) => {
