import { useState } from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { Menu, X, ChevronDown } from "lucide-react";
import logoImage from "../assets/logo.svg";
import ServicesMegaMenu from "@/components/ServicesMegaMenu";

export default function Navigation() {
  const [isOpen, setIsOpen] = useState(false);
  const [isServicesOpen, setIsServicesOpen] = useState(false);
  const [location] = useLocation();

  const navigation = [
    { name: "Technologies", href: "/technologies" },
    { name: "Portfolio", href: "/portfolio" },
    { name: "About", href: "/about" },
    { name: "Contact", href: "/contact" },
  ];

  const isActive = (href: string) => {
    if (href.startsWith("#")) {
      return false; // Hash links are handled differently
    }
    return location === href;
  };

  const handleNavClick = (href: string) => {
    setIsOpen(false);
    setIsServicesOpen(false);
    if (href.startsWith("#")) {
      // Smooth scroll for hash links
      const element = document.querySelector(href);
      if (element) {
        element.scrollIntoView({ behavior: "smooth" });
      }
    }
  };

  const handleServicesToggle = () => {
    setIsServicesOpen(!isServicesOpen);
  };

  const handleCloseServices = () => {
    setIsServicesOpen(false);
  };

  const handleMouseEnterServices = () => {
    setIsServicesOpen(true);
  };

  const handleMouseLeaveServices = () => {
    setIsServicesOpen(false);
  };

  return (
    <>
    <nav className="bg-white shadow-sm border-b border-gray-100 sticky top-0 z-50 relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex items-center">
            <Link href="/">
              <div className="flex items-center space-x-2" data-testid="link-home">
                <img 
                  src={logoImage} 
                  alt="Pi Tetris Logo" 
                  className="h-10 w-10 object-contain"
                />
                <span className="text-xl font-bold text-blue-600">Pi Tetris</span>
              </div>
            </Link>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:block">
            <div className="ml-10 flex items-baseline space-x-8">
              {/* Services Dropdown */}
              <div className="relative group">
                <button
                  onClick={handleServicesToggle}
                  onMouseEnter={handleMouseEnterServices}
                  className={`flex items-center px-3 py-2 text-sm font-medium transition-colors ${
                    location.startsWith('/services') 
                      ? "text-blue-600"
                      : "text-gray-600 hover:text-blue-600"
                  }`}
                  data-testid="nav-services"
                >
                  Services
                  <ChevronDown className={`ml-1 h-4 w-4 transform transition-transform ${
                    isServicesOpen ? 'rotate-180' : ''
                  }`} />
                </button>
              </div>

              {/* Other Navigation Items */}
              {navigation.map((item) => (
                item.href.startsWith("#") ? (
                  <button
                    key={item.name}
                    onClick={() => handleNavClick(item.href)}
                    className="text-gray-600 hover:text-blue-600 px-3 py-2 text-sm font-medium transition-colors"
                    data-testid={`nav-${item.name.toLowerCase()}`}
                  >
                    {item.name}
                  </button>
                ) : (
                  <Link key={item.name} href={item.href}>
                    <span
                      className={`px-3 py-2 text-sm font-medium transition-colors cursor-pointer ${
                        isActive(item.href)
                          ? "text-blue-600"
                          : "text-gray-600 hover:text-blue-600"
                      }`}
                      data-testid={`nav-${item.name.toLowerCase()}`}
                    >
                      {item.name}
                    </span>
                  </Link>
                )
              ))}
              <Link href="/contact">
                <Button data-testid="button-get-started">
                  Get Started
                </Button>
              </Link>
            </div>
          </div>

          {/* Mobile Navigation */}
          <div className="md:hidden">
            <Sheet open={isOpen} onOpenChange={setIsOpen}>
              <SheetTrigger asChild>
                <Button variant="ghost" size="sm" data-testid="button-mobile-menu">
                  <Menu className="h-6 w-6" />
                </Button>
              </SheetTrigger>
              <SheetContent side="right" className="w-80">
                <div className="flex items-center justify-between mb-8">
                  <div className="flex items-center space-x-2">
                    <img 
                      src={logoImage} 
                      alt="Pi Tetris Logo" 
                      className="h-8 w-8 object-contain"
                    />
                    <span className="text-lg font-semibold">Pi Tetris</span>
                  </div>
                  <Button variant="ghost" size="sm" onClick={() => setIsOpen(false)}>
                    <X className="h-5 w-5" />
                  </Button>
                </div>
                <nav className="space-y-4">
                  {/* Mobile Services Link */}
                  <Link href="/services">
                    <span
                      onClick={() => setIsOpen(false)}
                      className={`block py-2 text-base font-medium transition-colors cursor-pointer ${
                        location.startsWith('/services')
                          ? "text-blue-600"
                          : "text-gray-600 hover:text-blue-600"
                      }`}
                      data-testid="mobile-nav-services"
                    >
                      Services
                    </span>
                  </Link>

                  {/* Other Navigation Items */}
                  {navigation.map((item) => (
                    item.href.startsWith("#") ? (
                      <button
                        key={item.name}
                        onClick={() => handleNavClick(item.href)}
                        className="block w-full text-left text-gray-600 hover:text-blue-600 py-2 text-base font-medium transition-colors"
                        data-testid={`mobile-nav-${item.name.toLowerCase()}`}
                      >
                        {item.name}
                      </button>
                    ) : (
                      <Link key={item.name} href={item.href}>
                        <span
                          onClick={() => setIsOpen(false)}
                          className={`block py-2 text-base font-medium transition-colors cursor-pointer ${
                            isActive(item.href)
                              ? "text-blue-600"
                              : "text-gray-600 hover:text-blue-600"
                          }`}
                          data-testid={`mobile-nav-${item.name.toLowerCase()}`}
                        >
                          {item.name}
                        </span>
                      </Link>
                    )
                  ))}
                  <div className="pt-4">
                    <Link href="/contact">
                      <Button className="w-full" onClick={() => setIsOpen(false)} data-testid="mobile-button-get-started">
                        Get Started
                      </Button>
                    </Link>
                  </div>
                </nav>
              </SheetContent>
            </Sheet>
          </div>
        </div>
      </div>
    </nav>

    {/* Services Mega Menu - Positioned outside nav for full width */}
    <div 
      onMouseLeave={handleMouseLeaveServices}
      className={isServicesOpen ? "block" : "hidden"}
    >
      <ServicesMegaMenu isOpen={isServicesOpen} onClose={handleCloseServices} />
    </div>

    {/* Backdrop for mega menu */}
    {isServicesOpen && (
      <div 
        className="fixed inset-0 bg-black bg-opacity-10 z-40" 
        onClick={handleCloseServices}
      />
    )}
    </>
  );
}

client/src/components/ObjectUploader.tsx.content:
import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { DashboardModal } from "@uppy/react";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [showModal, setShowModal] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
      })
  );

  // Only allow mouse clicks, block all keyboard interaction
  const handleButtonClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setShowModal(true);
  };

  return (
    <div 
      style={{ isolation: 'isolate' }}
      onKeyDown={(e) => {
        // Block all keyboard events from reaching the button
        e.preventDefault();
        e.stopPropagation();
      }}
    >
      <div 
        role="button"
        onClick={handleButtonClick}
        className={`inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 bg-primary text-primary-foreground hover:bg-primary/90 h-10 px-4 py-2 cursor-pointer ${buttonClassName || ""}`}
        style={{ 
          userSelect: 'none',
          WebkitUserSelect: 'none',
          pointerEvents: 'auto'
        }}
        onKeyDown={(e) => {
          // Completely block keyboard events
          e.preventDefault();
          e.stopPropagation();
        }}
        onKeyUp={(e) => {
          e.preventDefault();
          e.stopPropagation();
        }}
        onFocus={(e) => {
          e.preventDefault();
          (e.target as HTMLElement).blur();
        }}
        tabIndex={-1}
      >
        {children}
      </div>

      <DashboardModal
        uppy={uppy}
        open={showModal}
        onRequestClose={() => setShowModal(false)}
        proudlyDisplayPoweredByUppy={false}
      />
    </div>
  );
}

client/src/components/RichTextEditor.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Bold, 
  Italic, 
  Underline, 
  List, 
  ListOrdered, 
  Link, 
  Eye,
  Edit3
} from "lucide-react";

interface RichTextEditorProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
}

export default function RichTextEditor({ value, onChange, placeholder }: RichTextEditorProps) {
  const [activeTab, setActiveTab] = useState<"edit" | "preview">("edit");
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const insertText = (before: string, after: string = "") => {
    const textarea = textareaRef.current;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const selectedText = value.substring(start, end);

    const newText = value.substring(0, start) + before + selectedText + after + value.substring(end);
    onChange(newText);

    // Reset cursor position
    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(start + before.length, end + before.length);
    }, 0);
  };

  const formatButtons = [
    { icon: Bold, action: () => insertText("**", "**"), label: "Bold" },
    { icon: Italic, action: () => insertText("*", "*"), label: "Italic" },
    { icon: List, action: () => insertText("\n- ", ""), label: "Bullet List" },
    { icon: ListOrdered, action: () => insertText("\n1. ", ""), label: "Numbered List" },
    { icon: Link, action: () => insertText("[", "](url)"), label: "Link" },
  ];

  // Simple markdown to HTML converter for preview
  const markdownToHtml = (markdown: string) => {
    let html = markdown
      // Headers
      .replace(/^### (.*$)/gim, '<h3 class="text-lg font-semibold mb-2">$1</h3>')
      .replace(/^## (.*$)/gim, '<h2 class="text-xl font-semibold mb-3">$1</h2>')
      .replace(/^# (.*$)/gim, '<h1 class="text-2xl font-bold mb-4">$1</h1>')
      // Bold
      .replace(/\*\*(.*?)\*\*/g, '<strong class="font-semibold">$1</strong>')
      // Italic
      .replace(/\*(.*?)\*/g, '<em class="italic">$1</em>')
      // Links
      .replace(/\[([^\]]+)\]\(([^)]+)\)/g, '<a href="$2" class="text-blue-600 hover:underline">$1</a>')
      // Line breaks
      .replace(/\n/g, '<br>');

    // Lists
    html = html.replace(/^- (.+)$/gm, '<li class="ml-4">â€¢ $1</li>');
    html = html.replace(/^(\d+)\. (.+)$/gm, '<li class="ml-4">$1. $2</li>');

    return html;
  };

  return (
    <Card>
      <CardContent className="p-0">
        <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as "edit" | "preview")}>
          <div className="border-b px-4 py-2">
            <div className="flex items-center justify-between">
              <div className="flex space-x-1">
                {formatButtons.map((button, index) => (
                  <Button
                    key={index}
                    type="button"
                    variant="ghost"
                    size="sm"
                    onClick={button.action}
                    title={button.label}
                    className="h-8 w-8 p-0"
                  >
                    <button.icon className="h-4 w-4" />
                  </Button>
                ))}
              </div>
              <TabsList className="grid w-fit grid-cols-2">
                <TabsTrigger value="edit" className="text-xs">
                  <Edit3 className="h-3 w-3 mr-1" />
                  Edit
                </TabsTrigger>
                <TabsTrigger value="preview" className="text-xs">
                  <Eye className="h-3 w-3 mr-1" />
                  Preview
                </TabsTrigger>
              </TabsList>
            </div>
          </div>

          <TabsContent value="edit" className="m-0">
            <Textarea
              ref={textareaRef}
              value={value}
              onChange={(e) => onChange(e.target.value)}
              placeholder={placeholder || "Enter your content here... You can use markdown formatting."}
              className="min-h-[200px] border-0 focus-visible:ring-0 resize-none rounded-none"
              style={{ fieldSizing: "content" }}
            />
          </TabsContent>

          <TabsContent value="preview" className="m-0">
            <div className="p-4 min-h-[200px] prose prose-sm max-w-none">
              {value ? (
                <div dangerouslySetInnerHTML={{ __html: markdownToHtml(value) }} />
              ) : (
                <p className="text-gray-500 italic">Nothing to preview yet...</p>
              )}
            </div>
          </TabsContent>
        </Tabs>

        <div className="px-4 py-2 bg-gray-50 border-t text-xs text-gray-600">
          <p>Tip: Use **bold**, *italic*, # headers, - lists, and [links](url) for formatting</p>
        </div>
      </CardContent>
    </Card>
  );
}

