      { name: 'React', url: '#' },
      { name: 'Vue.js', url: '#' },
      { name: 'Svelte', url: '#' },
      { name: 'TypeScript', url: '#' },
      { name: 'JavaScript', url: '#' }
    ],
    'ai-ml': [
      { name: 'OpenAI', url: '#' },
      { name: 'Anthropic', url: '#' },
      { name: 'TensorFlow', url: '#' },
      { name: 'PyTorch', url: '#' },
