          {clients.length > 0 ? (
            <motion.div 
              className="grid grid-cols-2 md:grid-cols-6 gap-8 items-center"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
