import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import RichTextEditor from "@/components/RichTextEditor";
import { SimpleFileUpload } from "@/components/SimpleFileUpload";
import { Plus, Edit, Trash2, Star, Eye, EyeOff } from "lucide-react";

export default function CaseStudyManager() {
  const [editingItem, setEditingItem] = useState<any>(null);
  const [isCreating, setIsCreating] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: caseStudies = [] } = useQuery({
    queryKey: ["/api/case-studies"],
  });

  const { data: categories = [] } = useQuery({
    queryKey: ["/api/categories"],
    queryFn: async () => {
      const response = await fetch('/api/categories?type=case-study');
      if (!response.ok) throw new Error('Failed to fetch categories');
      return response.json();
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/case-studies", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/case-studies"] });
      setIsCreating(false);
      setEditingItem(null);
      toast({ title: "Case study created successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error creating case study", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return apiRequest("PUT", `/api/admin/case-studies/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/case-studies"] });
      setEditingItem(null);
      toast({ title: "Case study updated successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error updating case study", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/case-studies/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/case-studies"] });
      toast({ title: "Case study deleted successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error deleting case study", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const handleCreate = () => {
    setIsCreating(true);
    setEditingItem({
      title: "",
      slug: "",
      clientName: "",
      shortDescription: "",
      longDescription: "",
      challenge: "",
      solution: "",
      results: "",
      imageUrl: "",
      categoryId: "",
      technologies: [],
      tags: [],
      projectDuration: "",
      teamSize: 1,
      sortOrder: 0,
      isFeatured: false,
      isActive: true,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingItem) return;

    // Parse technologies and tags from comma-separated strings
    const processedData = {
      ...editingItem,
      categoryId: editingItem.categoryId === '' ? null : editingItem.categoryId,
      technologies: typeof editingItem.technologies === 'string' 
        ? editingItem.technologies.split(',').map((t: string) => t.trim()).filter(Boolean)
        : editingItem.technologies || [],
      tags: typeof editingItem.tags === 'string'
        ? editingItem.tags.split(',').map((t: string) => t.trim()).filter(Boolean)
        : editingItem.tags || [],
    };

    if (isCreating) {
      createMutation.mutate(processedData);
    } else {
      updateMutation.mutate({ id: editingItem.id, data: processedData });
    }
  };

  const handleImageUpload = async (file: File) => {
    try {
      // Get upload URL
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json();

      // Upload file directly
      const uploadResponse = await fetch(data.uploadURL, {
        method: 'PUT',
        body: file,
        headers: {
          'Content-Type': file.type
        }
      });

      if (uploadResponse.ok) {
        // Extract clean URL without query parameters
        const cleanUrl = data.uploadURL.split('?')[0];
        setEditingItem({ ...editingItem, imageUrl: cleanUrl });
        toast({ title: "Image uploaded successfully" });
      } else {
        throw new Error('Upload failed');
      }
    } catch (error) {
      console.error("Error uploading image:", error);
      toast({ 
        title: "Upload failed", 
        description: "Please try again",
        variant: "destructive" 
      });
    }
  };

  const handleRemoveImage = () => {
    setEditingItem({ ...editingItem, imageUrl: "" });
  };

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Case Study Manager</h1>
        <p className="text-gray-600">Manage portfolio case studies and project showcases.</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle>Case Studies</CardTitle>
            <Button onClick={handleCreate} data-testid="button-add-case-study">
              <Plus className="w-4 h-4 mr-2" />
              Add Case Study
            </Button>
          </CardHeader>
          <CardContent>
            <div className="space-y-4 max-h-96 overflow-y-auto">
              {caseStudies.map((caseStudy: any) => (
                <div key={caseStudy.id} className="flex items-start justify-between p-4 border rounded-lg">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      <h3 className="font-medium">{caseStudy.title}</h3>
                      {caseStudy.isFeatured && <Star className="w-4 h-4 text-yellow-500 fill-current" />}
                    </div>
                    {caseStudy.clientName && (
                      <p className="text-sm text-blue-600 mb-1">Client: {caseStudy.clientName}</p>
                    )}
                    <p className="text-sm text-gray-600 mb-2">{caseStudy.shortDescription}</p>
                    <div className="flex items-center gap-2">
                      <Badge variant={caseStudy.isActive ? "default" : "secondary"}>
                        {caseStudy.isActive ? "Active" : "Inactive"}
                      </Badge>
                      {caseStudy.isFeatured && (
                        <Badge variant="outline" className="text-yellow-600 border-yellow-600">
                          Featured
                        </Badge>
                      )}
                      {caseStudy.isActive ? (
                        <Eye className="w-4 h-4 text-green-600" />
                      ) : (
                        <EyeOff className="w-4 h-4 text-gray-400" />
                      )}
                    </div>
                  </div>
                  <div className="flex gap-2 ml-4">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => setEditingItem(caseStudy)}
                      data-testid={`button-edit-case-study-${caseStudy.id}`}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => deleteMutation.mutate(caseStudy.id)}
                      data-testid={`button-delete-case-study-${caseStudy.id}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {editingItem && (
          <Card>
            <CardHeader>
              <CardTitle>{isCreating ? "Create Case Study" : "Edit Case Study"}</CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="title">Title</Label>
                    <Input
                      id="title"
                      value={editingItem.title || ""}
                      onChange={(e) => setEditingItem({ ...editingItem, title: e.target.value })}
                      required
                      data-testid="input-case-study-title"
                    />
                  </div>
                  <div>
                    <Label htmlFor="slug">Slug</Label>
                    <Input
                      id="slug"
                      value={editingItem.slug || ""}
                      onChange={(e) => setEditingItem({ ...editingItem, slug: e.target.value })}
                      required
                      data-testid="input-case-study-slug"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="clientName">Client Name</Label>
                  <Input
                    id="clientName"
                    value={editingItem.clientName || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, clientName: e.target.value })}
                    data-testid="input-case-study-client"
                  />
                </div>

                <div>
                  <Label htmlFor="shortDescription">Short Description</Label>
                  <Textarea
                    id="shortDescription"
                    value={editingItem.shortDescription || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, shortDescription: e.target.value })}
                    data-testid="textarea-case-study-short-description"
                  />
                </div>

                <div>
                  <Label>Project Image</Label>
                  <div className="mt-2">
                    <SimpleFileUpload
                      onFileSelect={handleImageUpload}
                      currentUrl={editingItem.imageUrl}
                      onRemove={handleRemoveImage}
                      maxSize={10}
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="longDescription">Detailed Description</Label>
                  <RichTextEditor
                    value={editingItem.longDescription || ""}
                    onChange={(value) => setEditingItem({ ...editingItem, longDescription: value })}
                  />
                </div>

                <div>
                  <Label htmlFor="challenge">Challenge</Label>
                  <RichTextEditor
                    value={editingItem.challenge || ""}
                    onChange={(value) => setEditingItem({ ...editingItem, challenge: value })}
                  />
                </div>

                <div>
                  <Label htmlFor="solution">Solution</Label>
                  <RichTextEditor
                    value={editingItem.solution || ""}
                    onChange={(value) => setEditingItem({ ...editingItem, solution: value })}
                  />
                </div>

                <div>
                  <Label htmlFor="results">Results</Label>
                  <RichTextEditor
                    value={editingItem.results || ""}
                    onChange={(value) => setEditingItem({ ...editingItem, results: value })}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="projectDuration">Project Duration</Label>
                    <Input
                      id="projectDuration"
                      value={editingItem.projectDuration || ""}
                      onChange={(e) => setEditingItem({ ...editingItem, projectDuration: e.target.value })}
                      placeholder="e.g., 6 months"
                      data-testid="input-case-study-duration"
                    />
                  </div>
                  <div>
                    <Label htmlFor="teamSize">Team Size</Label>
                    <Input
                      id="teamSize"
                      type="number"
                      min="1"
                      value={editingItem.teamSize || 1}
                      onChange={(e) => setEditingItem({ ...editingItem, teamSize: parseInt(e.target.value) })}
                      data-testid="input-case-study-team-size"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="technologies">Technologies (comma-separated)</Label>
                  <Input
                    id="technologies"
                    value={Array.isArray(editingItem.technologies) ? editingItem.technologies.join(', ') : editingItem.technologies || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, technologies: e.target.value })}
                    placeholder="React, Node.js, PostgreSQL"
                    data-testid="input-case-study-technologies"
                  />
                </div>

                <div>
                  <Label htmlFor="tags">Tags (comma-separated)</Label>
                  <Input
                    id="tags"
                    value={Array.isArray(editingItem.tags) ? editingItem.tags.join(', ') : editingItem.tags || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, tags: e.target.value })}
                    placeholder="AI, Web Development, Enterprise"
                    data-testid="input-case-study-tags"
                  />
                </div>

                <div>
                  <Label htmlFor="category">Category</Label>
                  <Select 
                    value={editingItem.categoryId || ""} 
                    onValueChange={(value) => setEditingItem({ ...editingItem, categoryId: value })}
                  >
                    <SelectTrigger data-testid="select-case-study-category">
                      <SelectValue placeholder="Select category" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="none">No category</SelectItem>
                      {categories.map((category: any) => (
                        <SelectItem key={category.id} value={category.id}>
                          {category.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex items-center space-x-4">
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isFeatured"
                      checked={editingItem.isFeatured || false}
                      onCheckedChange={(checked) => setEditingItem({ ...editingItem, isFeatured: checked })}
                      data-testid="switch-case-study-featured"
                    />
                    <Label htmlFor="isFeatured">Featured</Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isActive"
                      checked={editingItem.isActive || false}
                      onCheckedChange={(checked) => setEditingItem({ ...editingItem, isActive: checked })}
                      data-testid="switch-case-study-active"
                    />
                    <Label htmlFor="isActive">Active</Label>
                  </div>
                </div>

                <div className="flex gap-2">
                  <Button type="submit" disabled={createMutation.isPending || updateMutation.isPending} data-testid="button-save-case-study">
                    {isCreating ? "Create Case Study" : "Update Case Study"}
                  </Button>
                  <Button type="button" variant="outline" onClick={() => { setEditingItem(null); setIsCreating(false); }} data-testid="button-cancel-case-study">
                    Cancel
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin/CategoryManager.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Plus, Edit, Trash2, Eye, EyeOff, Folder } from "lucide-react";

export default function CategoryManager() {
  const [editingItem, setEditingItem] = useState<any>(null);
  const [isCreating, setIsCreating] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: categories = [] } = useQuery({
    queryKey: ["/api/categories"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/categories", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      setIsCreating(false);
      setEditingItem(null);
      toast({ title: "Category created successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error creating category", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return apiRequest("PUT", `/api/admin/categories/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      setEditingItem(null);
      toast({ title: "Category updated successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error updating category", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/categories/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      toast({ title: "Category deleted successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error deleting category", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const handleCreate = () => {
    setIsCreating(true);
    setEditingItem({
      name: "",
      slug: "",
      description: "",
      type: "service",
      sortOrder: 0,
      isActive: true,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingItem) return;

    if (isCreating) {
      createMutation.mutate(editingItem);
    } else {
      updateMutation.mutate({ id: editingItem.id, data: editingItem });
    }
  };

  const categoryTypes = [
    { value: "service", label: "Service" },
    { value: "case-study", label: "Case Study" },
    { value: "technology", label: "Technology" },
    { value: "blog", label: "Blog" },
    { value: "other", label: "Other" },
  ];

  const getCategoryTypeColor = (type: string) => {
    switch (type) {
      case "service":
        return "bg-blue-100 text-blue-800 border-blue-200";
      case "case-study":
        return "bg-green-100 text-green-800 border-green-200";
      case "technology":
        return "bg-purple-100 text-purple-800 border-purple-200";
      case "blog":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  // Group categories by type
  const categoriesByType = categories.reduce((acc: any, category: any) => {
    const type = category.type || 'other';
    if (!acc[type]) acc[type] = [];
    acc[type].push(category);
    return acc;
  }, {});

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Category Manager</h1>
        <p className="text-gray-600">Organize content with categories for services, case studies, and technologies.</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle>Categories</CardTitle>
            <Button onClick={handleCreate} data-testid="button-add-category">
              <Plus className="w-4 h-4 mr-2" />
              Add Category
            </Button>
          </CardHeader>
          <CardContent>
            <div className="space-y-6 max-h-96 overflow-y-auto">
              {Object.entries(categoriesByType).map(([type, typeCategories]: [string, any]) => (
                <div key={type}>
                  <h3 className="text-lg font-semibold text-gray-900 mb-3 capitalize flex items-center">
                    <Folder className="w-5 h-5 mr-2" />
                    {type.replace('-', ' ')} Categories
                  </h3>
                  <div className="space-y-3">
                    {typeCategories.map((category: any) => (
                      <div key={category.id} className="flex items-center justify-between p-3 border rounded-lg">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <h4 className="font-medium">{category.name}</h4>
                            <Badge className={getCategoryTypeColor(category.type)}>
                              {category.type}
                            </Badge>
                          </div>
                          <p className="text-sm text-gray-600 mb-2">{category.description}</p>
                          <div className="flex items-center gap-2">
                            <code className="text-xs bg-gray-100 px-2 py-1 rounded">{category.slug}</code>
                            <Badge variant={category.isActive ? "default" : "secondary"}>
                              {category.isActive ? "Active" : "Inactive"}
                            </Badge>
                            {category.isActive ? (
                              <Eye className="w-4 h-4 text-green-600" />
                            ) : (
                              <EyeOff className="w-4 h-4 text-gray-400" />
                            )}
                          </div>
                        </div>
                        <div className="flex gap-2 ml-4">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => setEditingItem(category)}
                            data-testid={`button-edit-category-${category.id}`}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="destructive"
                            onClick={() => deleteMutation.mutate(category.id)}
                            data-testid={`button-delete-category-${category.id}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}

              {categories.length === 0 && (
                <div className="text-center py-8 text-gray-500">
                  <Folder className="w-12 h-12 mx-auto mb-3 text-gray-300" />
                  <p>No categories created yet.</p>
                  <p className="text-sm">Create your first category to organize content.</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {editingItem && (
          <Card>
            <CardHeader>
              <CardTitle>{isCreating ? "Create Category" : "Edit Category"}</CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                  <Label htmlFor="name">Category Name</Label>
                  <Input
                    id="name"
                    value={editingItem.name || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, name: e.target.value })}
                    required
                    placeholder="e.g., AI & Machine Learning"
                    data-testid="input-category-name"
                  />
                </div>

                <div>
                  <Label htmlFor="slug">URL Slug</Label>
                  <Input
                    id="slug"
                    value={editingItem.slug || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, slug: e.target.value })}
                    required
                    placeholder="e.g., ai-machine-learning"
                    data-testid="input-category-slug"
                  />
                  <p className="text-sm text-gray-500 mt-1">
                    Used in URLs. Should be lowercase with hyphens instead of spaces.
                  </p>
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    value={editingItem.description || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, description: e.target.value })}
                    rows={3}
                    placeholder="Brief description of this category..."
                    data-testid="textarea-category-description"
                  />
                </div>

                <div>
                  <Label htmlFor="type">Category Type</Label>
                  <Select 
                    value={editingItem.type || ""} 
                    onValueChange={(value) => setEditingItem({ ...editingItem, type: value })}
                  >
                    <SelectTrigger data-testid="select-category-type">
                      <SelectValue placeholder="Select category type" />
                    </SelectTrigger>
                    <SelectContent>
                      {categoryTypes.map((type) => (
                        <SelectItem key={type.value} value={type.value}>
                          {type.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <p className="text-sm text-gray-500 mt-1">
                    Determines where this category can be used in the system.
                  </p>
                </div>

                <div>
                  <Label htmlFor="sortOrder">Sort Order</Label>
                  <Input
                    id="sortOrder"
                    type="number"
                    value={editingItem.sortOrder || 0}
                    onChange={(e) => setEditingItem({ ...editingItem, sortOrder: parseInt(e.target.value) })}
                    data-testid="input-category-sort-order"
                  />
                  <p className="text-sm text-gray-500 mt-1">
                    Lower numbers appear first. Use 0 for default ordering.
                  </p>
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    id="isActive"
                    checked={editingItem.isActive || false}
                    onCheckedChange={(checked) => setEditingItem({ ...editingItem, isActive: checked })}
                    data-testid="switch-category-active"
                  />
                  <Label htmlFor="isActive">Active</Label>
                  <p className="text-sm text-gray-500 ml-2">
                    Only active categories are visible to users.
                  </p>
                </div>

                <div className="flex gap-2">
                  <Button type="submit" disabled={createMutation.isPending || updateMutation.isPending} data-testid="button-save-category">
                    {isCreating ? "Create Category" : "Update Category"}
                  </Button>
                  <Button type="button" variant="outline" onClick={() => { setEditingItem(null); setIsCreating(false); }} data-testid="button-cancel-category">
                    Cancel
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        )}
      </div>

      {/* Category Usage Overview */}
      <Card className="mt-8">
        <CardHeader>
          <CardTitle>Category Usage Overview</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {categoryTypes.map((type) => {
              const typeCategories = categoriesByType[type.value] || [];
              const activeCount = typeCategories.filter((c: any) => c.isActive).length;

              return (
                <div key={type.value} className="text-center p-4 border rounded-lg">
                  <div className={`w-12 h-12 rounded-full mx-auto mb-2 flex items-center justify-center ${getCategoryTypeColor(type.value)}`}>
                    <Folder className="w-6 h-6" />
                  </div>
                  <h3 className="font-semibold">{type.label}</h3>
                  <p className="text-sm text-gray-600">
                    {activeCount} active / {typeCategories.length} total
                  </p>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/admin/ClientManager.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import AdminLayout from "@/components/AdminLayout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import { Plus, Edit, Trash2, Save, Building, Image } from "lucide-react";

export default function ClientManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingId, setEditingId] = useState<string | null>(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    logoUrl: "",
    website: "",
    description: "",
    isActive: true,
    isFeatured: false,
    sortOrder: 0
  });

  const { data: clients, isLoading } = useQuery({
    queryKey: ["/api/admin/clients"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      await apiRequest("POST", "/api/admin/clients", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/clients"] });
      resetForm();
      toast({
        title: "Client created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error creating client",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      await apiRequest("PUT", `/api/admin/clients/${editingId}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/clients"] });
      resetForm();
      toast({
        title: "Client updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error updating client",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/admin/clients/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/clients"] });
      toast({
        title: "Client deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error deleting client",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      logoUrl: "",
      website: "",
      description: "",
      isActive: true,
      isFeatured: false,
      sortOrder: 0
    });
    setEditingId(null);
    setShowForm(false);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingId) {
      updateMutation.mutate(formData);
    } else {
      createMutation.mutate(formData);
    }
  };

  const startEdit = (client: any) => {
    setEditingId(client.id);
    setFormData({
      name: client.name || "",
      logoUrl: client.logoUrl || "",
      website: client.website || "",
      description: client.description || "",
      isActive: client.isActive ?? true,
      isFeatured: client.isFeatured || false,
      sortOrder: client.sortOrder || 0
    });
    setShowForm(true);
  };

  const handleLogoUpload = async () => {
    try {
      const response = await fetch("/api/objects/upload", {
        method: "POST",
        credentials: "include",
      });
      const data = await response.json();
      return { method: "PUT" as const, url: data.uploadURL };
    } catch (error) {
      console.error("Error getting upload URL:", error);
      throw error;
    }
  };

  const handleLogoUploadComplete = async (result: any) => {
    if (result.successful && result.successful[0]) {
      const uploadURL = result.successful[0].uploadURL;

      try {
        const response = await fetch("/api/objects/set-acl", {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          credentials: "include",
          body: JSON.stringify({
            objectURL: uploadURL,
            visibility: "public"
          }),
        });

        const data = await response.json();
        setFormData({ ...formData, logoUrl: data.objectPath });
        toast({
          title: "Logo uploaded successfully",
        });
      } catch (error) {
        console.error("Error setting ACL:", error);
        toast({
          title: "Error processing logo",
          variant: "destructive",
        });
      }
    }
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className="flex items-center justify-center min-h-96">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pi-blue"></div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="space-y-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white" data-testid="text-page-title">
              Client Management
            </h1>
            <p className="text-pi-gray mt-2" data-testid="text-page-description">
              Manage client companies and their information
            </p>
          </div>
          <Button onClick={() => setShowForm(!showForm)} data-testid="button-add-client">
            <Plus className="w-4 h-4 mr-2" />
            Add Client
          </Button>
        </div>

        {/* Add/Edit Form */}
        {showForm && (
          <Card>
            <CardHeader>
              <CardTitle data-testid="text-form-title">
                {editingId ? "Edit Client" : "Add New Client"}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="name">Company Name *</Label>
                    <Input
                      id="name"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      required
                      data-testid="input-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="website">Website</Label>
                    <Input
                      id="website"
                      type="url"
                      value={formData.website}
                      onChange={(e) => setFormData({ ...formData, website: e.target.value })}
                      placeholder="https://company.com"
                      data-testid="input-website"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    rows={3}
                    placeholder="Brief description of the client or project work"
                    data-testid="textarea-description"
                  />
                </div>

                <div>
                  <Label>Company Logo</Label>
                  <div className="mt-2 space-y-2">
                    {formData.logoUrl && (
                      <div className="border border-gray-200 dark:border-gray-700 rounded-lg p-2">
                        <img src={formData.logoUrl} alt="Preview" className="h-12 w-32 object-contain" />
                        <p className="text-xs text-pi-gray mt-1" data-testid="text-current-logo">
                          Current logo
                        </p>
                      </div>
                    )}
                    <ObjectUploader
                      maxNumberOfFiles={1}
                      maxFileSize={5242880} // 5MB
                      onGetUploadParameters={handleLogoUpload}
                      onComplete={handleLogoUploadComplete}
                      data-testid="uploader-logo"
                    >
                      <div className="flex items-center gap-2">
                        <Image className="w-4 h-4" />
                        <span>Upload Logo</span>
                      </div>
                    </ObjectUploader>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="isActive"
                      checked={formData.isActive}
                      onCheckedChange={(checked) => setFormData({ ...formData, isActive: !!checked })}
                      data-testid="checkbox-isActive"
                    />
                    <Label htmlFor="isActive">Active</Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="isFeatured"
                      checked={formData.isFeatured}
                      onCheckedChange={(checked) => setFormData({ ...formData, isFeatured: !!checked })}
                      data-testid="checkbox-isFeatured"
                    />
                    <Label htmlFor="isFeatured">Featured</Label>
                  </div>
                  <div>
                    <Label htmlFor="sortOrder">Sort Order</Label>
                    <Input
                      id="sortOrder"
                      type="number"
                      value={formData.sortOrder}
                      onChange={(e) => setFormData({ ...formData, sortOrder: parseInt(e.target.value) || 0 })}
                      data-testid="input-sortOrder"
                    />
                  </div>
                </div>

                <div className="flex space-x-2">
                  <Button 
                    type="submit" 
                    disabled={createMutation.isPending || updateMutation.isPending}
                    data-testid="button-save"
                  >
                    <Save className="w-4 h-4 mr-2" />
                    {editingId ? "Update" : "Create"}
                  </Button>
                  <Button type="button" variant="outline" onClick={resetForm} data-testid="button-cancel">
                    Cancel
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        )}

        {/* Clients List */}
        <Card>
          <CardHeader>
            <CardTitle data-testid="text-clients-title">Clients</CardTitle>
          </CardHeader>
          <CardContent>
            {!clients || clients.length === 0 ? (
              <p className="text-pi-gray text-center py-8" data-testid="text-no-clients">
                No clients found. Add your first client above.
              </p>
            ) : (
              <div className="space-y-4">
                {clients.map((client: any) => (
                  <div 
                    key={client.id} 
                    className="border border-gray-200 dark:border-gray-700 rounded-lg p-4"
                    data-testid={`client-${client.name.toLowerCase().replace(/\s+/g, '-')}`}
                  >
                    <div className="flex items-start space-x-4">
                      <div className="flex-shrink-0">
                        {client.logoUrl ? (
                          <img 
                            src={client.logoUrl} 
                            alt={client.name}
                            className="w-16 h-12 object-contain"
                          />
                        ) : (
                          <div className="w-16 h-12 bg-gray-200 dark:bg-gray-700 rounded flex items-center justify-center">
                            <Building className="w-6 h-6 text-gray-400" />
                          </div>
                        )}
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-1">
                          <h3 className="font-semibold text-gray-900 dark:text-white" data-testid={`text-client-name-${client.name.toLowerCase().replace(/\s+/g, '-')}`}>
                            {client.name}
                          </h3>
                          <div className="flex space-x-1">
                            {client.isFeatured && (
                              <span className="text-xs px-2 py-1 bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200 rounded" data-testid={`badge-featured-${client.name.toLowerCase().replace(/\s+/g, '-')}`}>
                                Featured
                              </span>
                            )}
                            {!client.isActive && (
                              <span className="text-xs px-2 py-1 bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200 rounded" data-testid={`badge-inactive-${client.name.toLowerCase().replace(/\s+/g, '-')}`}>
                                Inactive
                              </span>
                            )}
                          </div>
                        </div>
                        {client.website && (
                          <a 
                            href={client.website} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="text-sm text-pi-blue hover:underline mb-1 block"
                            data-testid={`link-website-${client.name.toLowerCase().replace(/\s+/g, '-')}`}
                          >
                            {client.website}
                          </a>
                        )}
                        {client.description && (
                          <p className="text-sm text-gray-700 dark:text-gray-300" data-testid={`text-client-description-${client.name.toLowerCase().replace(/\s+/g, '-')}`}>
                            {client.description}
                          </p>
                        )}
                      </div>
                      <div className="flex space-x-2">
                        <Button 
                          variant="outline" 
                          size="sm" 
                          onClick={() => startEdit(client)}
                          data-testid={`button-edit-${client.name.toLowerCase().replace(/\s+/g, '-')}`}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button 
                          variant="outline" 
                          size="sm" 
                          onClick={() => deleteMutation.mutate(client.id)}
                          data-testid={`button-delete-${client.name.toLowerCase().replace(/\s+/g, '-')}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </AdminLayout>
  );
}

