import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import AdminLayout from "@/components/AdminLayout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Mail, Eye, Archive, MessageSquare, Calendar, Building, User, Phone } from "lucide-react";

export default function ContactFormManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedStatus, setSelectedStatus] = useState<string>("");
  const [selectedSubmission, setSelectedSubmission] = useState<any>(null);

  const { data: submissions, isLoading } = useQuery({
    queryKey: ["/api/admin/contact-submissions", selectedStatus ? { status: selectedStatus } : {}],
  });

  const markAsReadMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("PUT", `/api/admin/contact-submissions/${id}/read`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/contact-submissions"] });
      toast({
        title: "Marked as read",
      });
    },
    onError: () => {
      toast({
        title: "Error marking as read",
        variant: "destructive",
      });
    },
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      await apiRequest("PUT", `/api/admin/contact-submissions/${id}`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/contact-submissions"] });
      toast({
        title: "Status updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error updating status",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'new': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'read': return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
      case 'replied': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'archived': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  const getProjectTypeLabel = (projectType: string) => {
    const types: Record<string, string> = {
      'ai-ml': 'AI & Machine Learning',
      'custom-software': 'Custom Software Development',
      'mobile-app': 'Mobile App Development',
      'data-engineering': 'Data Engineering',
      'erp-crm': 'ERP/CRM/CMS Development',
      'staff-augmentation': 'Staff Augmentation',
      'consulting': 'Consulting'
    };
    return types[projectType] || projectType;
  };

  const handleStatusChange = (submissionId: string, newStatus: string) => {
    updateStatusMutation.mutate({ id: submissionId, status: newStatus });
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className="flex items-center justify-center min-h-96">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pi-blue"></div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="space-y-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white" data-testid="text-page-title">
              Contact Form Management
            </h1>
            <p className="text-pi-gray mt-2" data-testid="text-page-description">
              Manage and respond to contact form submissions
            </p>
          </div>
        </div>

        {/* Filter */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-4">
              <MessageSquare className="w-5 h-5 text-pi-blue" />
              <span className="font-medium">Filter by Status:</span>
              <Select value={selectedStatus} onValueChange={setSelectedStatus}>
                <SelectTrigger className="w-48" data-testid="select-status-filter">
                  <SelectValue placeholder="All submissions" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Submissions</SelectItem>
                  <SelectItem value="new">New</SelectItem>
                  <SelectItem value="read">Read</SelectItem>
                  <SelectItem value="replied">Replied</SelectItem>
                  <SelectItem value="archived">Archived</SelectItem>
                </SelectContent>
              </Select>
              {submissions && (
                <span className="text-sm text-pi-gray">
                  ({submissions.length} {submissions.length === 1 ? 'submission' : 'submissions'})
                </span>
              )}
            </div>
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Submissions List */}
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle data-testid="text-submissions-title">Contact Submissions</CardTitle>
              </CardHeader>
              <CardContent>
                {!submissions || submissions.length === 0 ? (
                  <div className="text-center py-12">
                    <Mail className="w-12 h-12 text-pi-gray mx-auto mb-4" />
                    <p className="text-pi-gray text-lg" data-testid="text-no-submissions">
                      {selectedStatus ? `No ${selectedStatus} submissions found.` : 'No contact submissions yet.'}
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {submissions.map((submission: any) => (
                      <div 
                        key={submission.id}
                        className={`border border-gray-200 dark:border-gray-700 rounded-lg p-4 cursor-pointer transition-colors ${
                          selectedSubmission?.id === submission.id ? 'bg-blue-50 dark:bg-blue-900/20 border-pi-blue' : 'hover:bg-gray-50 dark:hover:bg-gray-800'
                        }`}
                        onClick={() => setSelectedSubmission(submission)}
                        data-testid={`submission-${submission.id}`}
                      >
                        <div className="flex justify-between items-start mb-2">
                          <div className="flex items-center space-x-3">
                            <div>
                              <h3 className="font-semibold text-gray-900 dark:text-white" data-testid={`text-submission-name-${submission.id}`}>
                                {submission.firstName} {submission.lastName}
                              </h3>
                              <p className="text-sm text-pi-gray" data-testid={`text-submission-email-${submission.id}`}>
                                {submission.email}
                              </p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-2">
                            <Badge className={getStatusColor(submission.status)} data-testid={`badge-status-${submission.id}`}>
                              {submission.status || 'new'}
                            </Badge>
                            {!submission.isRead && (
                              <div className="w-2 h-2 bg-blue-500 rounded-full" data-testid={`indicator-unread-${submission.id}`}></div>
                            )}
                          </div>
                        </div>

                        <div className="flex items-center space-x-4 text-xs text-pi-gray mb-2">
                          <div className="flex items-center space-x-1">
                            <Calendar className="w-3 h-3" />
                            <span data-testid={`text-submission-date-${submission.id}`}>
                              {new Date(submission.createdAt).toLocaleDateString()}
                            </span>
                          </div>
                          {submission.company && (
                            <div className="flex items-center space-x-1">
                              <Building className="w-3 h-3" />
                              <span data-testid={`text-submission-company-${submission.id}`}>
                                {submission.company}
                              </span>
                            </div>
                          )}
                          {submission.projectType && (
                            <div className="flex items-center space-x-1">
                              <span className="w-3 h-3 bg-pi-blue rounded-full"></span>
                              <span data-testid={`text-submission-project-${submission.id}`}>
                                {getProjectTypeLabel(submission.projectType)}
                              </span>
                            </div>
                          )}
                        </div>

                        <p className="text-sm text-gray-700 dark:text-gray-300 line-clamp-2" data-testid={`text-submission-message-${submission.id}`}>
                          {submission.message}
                        </p>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Submission Details */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle data-testid="text-details-title">Submission Details</CardTitle>
              </CardHeader>
              <CardContent>
                {!selectedSubmission ? (
                  <div className="text-center py-12">
                    <Eye className="w-12 h-12 text-pi-gray mx-auto mb-4" />
                    <p className="text-pi-gray" data-testid="text-select-submission">
                      Select a submission to view details
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <div>
                      <h3 className="font-semibold text-gray-900 dark:text-white mb-2" data-testid="text-contact-info">
                        Contact Information
                      </h3>
                      <div className="space-y-2 text-sm">
                        <div className="flex items-center space-x-2">
                          <User className="w-4 h-4 text-pi-gray" />
                          <span data-testid="text-selected-name">
                            {selectedSubmission.firstName} {selectedSubmission.lastName}
                          </span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Mail className="w-4 h-4 text-pi-gray" />
                          <a 
                            href={`mailto:${selectedSubmission.email}`}
                            className="text-pi-blue hover:underline"
                            data-testid="link-selected-email"
                          >
                            {selectedSubmission.email}
                          </a>
                        </div>
                        {selectedSubmission.company && (
                          <div className="flex items-center space-x-2">
                            <Building className="w-4 h-4 text-pi-gray" />
                            <span data-testid="text-selected-company">{selectedSubmission.company}</span>
                          </div>
                        )}
                        <div className="flex items-center space-x-2">
                          <Calendar className="w-4 h-4 text-pi-gray" />
                          <span data-testid="text-selected-date">
                            {new Date(selectedSubmission.createdAt).toLocaleString()}
                          </span>
                        </div>
                      </div>
                    </div>

                    {selectedSubmission.projectType && (
                      <div>
                        <h3 className="font-semibold text-gray-900 dark:text-white mb-2">Project Type</h3>
                        <Badge variant="outline" data-testid="badge-selected-project">
                          {getProjectTypeLabel(selectedSubmission.projectType)}
                        </Badge>
                      </div>
                    )}

                    <div>
                      <h3 className="font-semibold text-gray-900 dark:text-white mb-2">Message</h3>
                      <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-3">
                        <p className="text-sm text-gray-700 dark:text-gray-300 whitespace-pre-wrap" data-testid="text-selected-message">
                          {selectedSubmission.message}
                        </p>
                      </div>
                    </div>

                    <div>
                      <h3 className="font-semibold text-gray-900 dark:text-white mb-2">Status</h3>
                      <Select 
                        value={selectedSubmission.status || 'new'} 
                        onValueChange={(value) => handleStatusChange(selectedSubmission.id, value)}
                      >
                        <SelectTrigger data-testid="select-selected-status">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="new">New</SelectItem>
                          <SelectItem value="read">Read</SelectItem>
                          <SelectItem value="replied">Replied</SelectItem>
                          <SelectItem value="archived">Archived</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="space-y-2">
                      {!selectedSubmission.isRead && (
                        <Button
                          variant="outline"
                          size="sm"
                          className="w-full"
                          onClick={() => markAsReadMutation.mutate(selectedSubmission.id)}
                          disabled={markAsReadMutation.isPending}
                          data-testid="button-mark-read"
                        >
                          <Eye className="w-4 h-4 mr-2" />
                          Mark as Read
                        </Button>
                      )}

                      <Button
                        variant="default"
                        size="sm"
                        className="w-full"
                        onClick={() => window.open(`mailto:${selectedSubmission.email}?subject=Re: Your inquiry to Pi Tetris`)}
                        data-testid="button-reply"
                      >
                        <Mail className="w-4 h-4 mr-2" />
                        Reply via Email
                      </Button>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </AdminLayout>
  );
}

client/src/pages/admin/ContentManager.ts.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import RichTextEditor from "@/components/RichTextEditor";
import { ObjectUploader } from "@/components/ObjectUploader";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Plus, Edit, Trash2, Eye, EyeOff } from "lucide-react";

export default function ContentManager() {
  const [activeTab, setActiveTab] = useState("services");
  const [editingItem, setEditingItem] = useState<any>(null);
  const [isCreating, setIsCreating] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: services = [] } = useQuery({
    queryKey: ["/api/services"],
  });

  const { data: clients = [] } = useQuery({
    queryKey: ["/api/clients"],
  });

  const { data: technologies = [] } = useQuery({
    queryKey: ["/api/technologies"],
  });

  const { data: categories = [] } = useQuery({
    queryKey: ["/api/categories"],
  });

  const createServiceMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/services", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setIsCreating(false);
      setEditingItem(null);
      toast({ title: "Service created successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error creating service", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const updateServiceMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return apiRequest("PUT", `/api/admin/services/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setEditingItem(null);
      toast({ title: "Service updated successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error updating service", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const deleteServiceMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/services/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      toast({ title: "Service deleted successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error deleting service", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const createClientMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/clients", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      setIsCreating(false);
      setEditingItem(null);
      toast({ title: "Client created successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error creating client", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const updateClientMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return apiRequest("PUT", `/api/admin/clients/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      setEditingItem(null);
      toast({ title: "Client updated successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error updating client", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const deleteClientMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/clients/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      toast({ title: "Client deleted successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error deleting client", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const handleCreateService = () => {
    setIsCreating(true);
    setActiveTab("services");
    setEditingItem({
      title: "",
      slug: "",
      shortDescription: "",
      longDescription: "",
      iconUrl: "",
      categoryId: "",
      features: [],
      technologies: [],
      sortOrder: 0,
      isActive: true,
    });
  };

  const handleCreateClient = () => {
    setIsCreating(true);
    setActiveTab("clients");
    setEditingItem({
      name: "",
      logoUrl: "",
      websiteUrl: "",
      description: "",
      sortOrder: 0,
      isActive: true,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingItem) return;

    if (activeTab === "services") {
      if (isCreating) {
        createServiceMutation.mutate(editingItem);
      } else {
        updateServiceMutation.mutate({ id: editingItem.id, data: editingItem });
      }
    } else if (activeTab === "clients") {
      if (isCreating) {
        createClientMutation.mutate(editingItem);
      } else {
        updateClientMutation.mutate({ id: editingItem.id, data: editingItem });
      }
    }
  };

  const handleImageUpload = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json();
      return { method: "PUT" as const, url: data.uploadURL };
    } catch (error) {
      toast({
        title: "Error getting upload URL",
        description: "Failed to prepare file upload",
        variant: "destructive",
      });
      throw error;
    }
  };

  const handleImageUploadComplete = async (result: any) => {
    if (result.successful && result.successful.length > 0) {
      const uploadedFile = result.successful[0];
      try {
        const response = await apiRequest("PUT", "/api/admin/images", {
          imageURL: uploadedFile.uploadURL,
        });
        const data = await response.json();

        if (activeTab === "services") {
          setEditingItem({ ...editingItem, iconUrl: data.objectPath });
        } else if (activeTab === "clients") {
          setEditingItem({ ...editingItem, logoUrl: data.objectPath });
        }

        toast({ title: "Image uploaded successfully" });
      } catch (error) {
        toast({
          title: "Error processing image",
          description: "Failed to save image",
          variant: "destructive",
        });
      }
    }
  };

  const renderServiceForm = () => (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="title">Title</Label>
          <Input
            id="title"
            value={editingItem?.title || ""}
            onChange={(e) => setEditingItem({ ...editingItem, title: e.target.value })}
            required
            data-testid="input-service-title"
          />
        </div>
        <div>
          <Label htmlFor="slug">Slug</Label>
          <Input
            id="slug"
            value={editingItem?.slug || ""}
            onChange={(e) => setEditingItem({ ...editingItem, slug: e.target.value })}
            required
            data-testid="input-service-slug"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="shortDescription">Short Description</Label>
        <Textarea
          id="shortDescription"
          value={editingItem?.shortDescription || ""}
          onChange={(e) => setEditingItem({ ...editingItem, shortDescription: e.target.value })}
          data-testid="textarea-service-short-description"
        />
      </div>

      <div>
        <Label htmlFor="longDescription">Long Description</Label>
        <RichTextEditor
          value={editingItem?.longDescription || ""}
          onChange={(value) => setEditingItem({ ...editingItem, longDescription: value })}
        />
      </div>

      <div>
        <Label>Icon Image</Label>
        <div className="mt-2">
          <ObjectUploader
            onGetUploadParameters={handleImageUpload}
            onComplete={handleImageUploadComplete}
            maxNumberOfFiles={1}
            maxFileSize={5 * 1024 * 1024} // 5MB
          >
            <span>Upload Icon</span>
          </ObjectUploader>
          {editingItem?.iconUrl && (
            <div className="mt-2">
              <img src={editingItem.iconUrl} alt="Icon preview" className="w-16 h-16 object-cover rounded" />
            </div>
          )}
        </div>
      </div>

      <div>
        <Label htmlFor="category">Category</Label>
        <Select 
          value={editingItem?.categoryId || ""} 
          onValueChange={(value) => setEditingItem({ ...editingItem, categoryId: value })}
        >
          <SelectTrigger data-testid="select-service-category">
            <SelectValue placeholder="Select category" />
          </SelectTrigger>
          <SelectContent>
            {categories.map((category: any) => (
              <SelectItem key={category.id} value={category.id}>
                {category.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div className="flex items-center space-x-2">
        <Switch
          id="isActive"
          checked={editingItem?.isActive || false}
          onCheckedChange={(checked) => setEditingItem({ ...editingItem, isActive: checked })}
          data-testid="switch-service-active"
        />
        <Label htmlFor="isActive">Active</Label>
      </div>

      <div className="flex gap-2">
        <Button type="submit" disabled={createServiceMutation.isPending || updateServiceMutation.isPending} data-testid="button-save-service">
          {isCreating ? "Create Service" : "Update Service"}
        </Button>
        <Button type="button" variant="outline" onClick={() => { setEditingItem(null); setIsCreating(false); }} data-testid="button-cancel-service">
          Cancel
        </Button>
      </div>
    </form>
  );

  const renderClientForm = () => (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <Label htmlFor="name">Client Name</Label>
        <Input
          id="name"
          value={editingItem?.name || ""}
          onChange={(e) => setEditingItem({ ...editingItem, name: e.target.value })}
          required
          data-testid="input-client-name"
        />
      </div>

      <div>
        <Label>Logo</Label>
        <div className="mt-2">
          <ObjectUploader
            onGetUploadParameters={handleImageUpload}
            onComplete={handleImageUploadComplete}
            maxNumberOfFiles={1}
            maxFileSize={5 * 1024 * 1024} // 5MB
          >
            <span>Upload Logo</span>
          </ObjectUploader>
          {editingItem?.logoUrl && (
            <div className="mt-2">
              <img src={editingItem.logoUrl} alt="Logo preview" className="w-32 h-16 object-contain rounded border" />
            </div>
          )}
        </div>
      </div>

      <div>
        <Label htmlFor="websiteUrl">Website URL</Label>
        <Input
          id="websiteUrl"
          type="url"
          value={editingItem?.websiteUrl || ""}
          onChange={(e) => setEditingItem({ ...editingItem, websiteUrl: e.target.value })}
          data-testid="input-client-website"
        />
      </div>

      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={editingItem?.description || ""}
          onChange={(e) => setEditingItem({ ...editingItem, description: e.target.value })}
          data-testid="textarea-client-description"
        />
      </div>

      <div className="flex items-center space-x-2">
        <Switch
          id="isActive"
          checked={editingItem?.isActive || false}
          onCheckedChange={(checked) => setEditingItem({ ...editingItem, isActive: checked })}
          data-testid="switch-client-active"
        />
        <Label htmlFor="isActive">Active</Label>
      </div>

      <div className="flex gap-2">
        <Button type="submit" disabled={createClientMutation.isPending || updateClientMutation.isPending} data-testid="button-save-client">
          {isCreating ? "Create Client" : "Update Client"}
        </Button>
        <Button type="button" variant="outline" onClick={() => { setEditingItem(null); setIsCreating(false); }} data-testid="button-cancel-client">
          Cancel
        </Button>
      </div>
    </form>
  );

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Content Manager</h1>
        <p className="text-gray-600">Manage services, clients, and other website content.</p>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="mb-8">
          <TabsTrigger value="services" data-testid="tab-services">Services</TabsTrigger>
          <TabsTrigger value="clients" data-testid="tab-clients">Clients</TabsTrigger>
          <TabsTrigger value="technologies" data-testid="tab-technologies">Technologies</TabsTrigger>
        </TabsList>

        <TabsContent value="services">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between">
                <CardTitle>Services</CardTitle>
                <Button onClick={handleCreateService} data-testid="button-add-service">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Service
                </Button>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {services.map((service: any) => (
                    <div key={service.id} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex-1">
                        <h3 className="font-medium">{service.title}</h3>
                        <p className="text-sm text-gray-600">{service.shortDescription}</p>
                        <div className="flex items-center gap-2 mt-2">
                          <Badge variant={service.isActive ? "default" : "secondary"}>
                            {service.isActive ? "Active" : "Inactive"}
                          </Badge>
                          {service.isActive ? (
                            <Eye className="w-4 h-4 text-green-600" />
                          ) : (
                            <EyeOff className="w-4 h-4 text-gray-400" />
                          )}
                        </div>
                      </div>
                      <div className="flex gap-2">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => setEditingItem(service)}
                          data-testid={`button-edit-service-${service.id}`}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          size="sm"
                          variant="destructive"
                          onClick={() => deleteServiceMutation.mutate(service.id)}
                          data-testid={`button-delete-service-${service.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {editingItem && activeTab === "services" && (
              <Card>
                <CardHeader>
                  <CardTitle>{isCreating ? "Create Service" : "Edit Service"}</CardTitle>
                </CardHeader>
                <CardContent>
                  {renderServiceForm()}
                </CardContent>
              </Card>
            )}
          </div>
        </TabsContent>

        <TabsContent value="clients">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between">
                <CardTitle>Clients</CardTitle>
                <Button onClick={handleCreateClient} data-testid="button-add-client">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Client
                </Button>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {clients.map((client: any) => (
                    <div key={client.id} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex items-center gap-4">
                        {client.logoUrl && (
                          <img src={client.logoUrl} alt={client.name} className="w-12 h-8 object-contain" />
                        )}
                        <div>
                          <h3 className="font-medium">{client.name}</h3>
                          <p className="text-sm text-gray-600">{client.description}</p>
                          <Badge variant={client.isActive ? "default" : "secondary"}>
                            {client.isActive ? "Active" : "Inactive"}
                          </Badge>
                        </div>
                      </div>
                      <div className="flex gap-2">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => setEditingItem(client)}
                          data-testid={`button-edit-client-${client.id}`}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          size="sm"
                          variant="destructive"
                          onClick={() => deleteClientMutation.mutate(client.id)}
                          data-testid={`button-delete-client-${client.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            {editingItem && activeTab === "clients" && (
              <Card>
                <CardHeader>
                  <CardTitle>{isCreating ? "Create Client" : "Edit Client"}</CardTitle>
                </CardHeader>
                <CardContent>
                  {renderClientForm()}
                </CardContent>
              </Card>
            )}
          </div>
        </TabsContent>

        <TabsContent value="technologies">
          <Card>
            <CardHeader>
              <CardTitle>Technologies</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-center py-8 text-gray-500">
                Technology management will be available in the next update.
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/admin/TeamManager.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import { Plus, Edit, Trash2, Eye, EyeOff, Linkedin, Github, Twitter } from "lucide-react";

export default function TeamManager() {
  const [editingItem, setEditingItem] = useState<any>(null);
  const [isCreating, setIsCreating] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: teamMembers = [] } = useQuery({
    queryKey: ["/api/team"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/team", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/team"] });
      setIsCreating(false);
      setEditingItem(null);
      toast({ title: "Team member created successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error creating team member", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return apiRequest("PUT", `/api/admin/team/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/team"] });
      setEditingItem(null);
      toast({ title: "Team member updated successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error updating team member", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/team/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/team"] });
      toast({ title: "Team member deleted successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error deleting team member", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const handleCreate = () => {
    setIsCreating(true);
    setEditingItem({
      name: "",
      role: "",
      bio: "",
      expertise: [],
      imageUrl: "",
      linkedinUrl: "",
      githubUrl: "",
      twitterUrl: "",
      sortOrder: 0,
      isActive: true,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingItem) return;

    // Parse expertise from comma-separated string
    const processedData = {
      ...editingItem,
      expertise: typeof editingItem.expertise === 'string' 
        ? editingItem.expertise.split(',').map((e: string) => e.trim()).filter(Boolean)
        : editingItem.expertise || [],
    };

    if (isCreating) {
      createMutation.mutate(processedData);
    } else {
      updateMutation.mutate({ id: editingItem.id, data: processedData });
    }
  };

  const handleImageUpload = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json();
      return { method: "PUT" as const, url: data.uploadURL };
    } catch (error) {
      toast({
        title: "Error getting upload URL",
        description: "Failed to prepare file upload",
        variant: "destructive",
      });
      throw error;
    }
  };

  const handleImageUploadComplete = async (result: any) => {
    if (result.successful && result.successful.length > 0) {
      const uploadedFile = result.successful[0];
      try {
        const response = await apiRequest("PUT", "/api/admin/images", {
          imageURL: uploadedFile.uploadURL,
        });
        const data = await response.json();
        setEditingItem({ ...editingItem, imageUrl: data.objectPath });
        toast({ title: "Image uploaded successfully" });
      } catch (error) {
        toast({
          title: "Error processing image",
          description: "Failed to save image",
          variant: "destructive",
        });
      }
    }
  };

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Team Manager</h1>
        <p className="text-gray-600">Manage team members and their profiles.</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle>Team Members</CardTitle>
            <Button onClick={handleCreate} data-testid="button-add-team-member">
              <Plus className="w-4 h-4 mr-2" />
              Add Team Member
            </Button>
          </CardHeader>
          <CardContent>
            <div className="space-y-4 max-h-96 overflow-y-auto">
              {teamMembers.map((member: any) => (
                <div key={member.id} className="flex items-start justify-between p-4 border rounded-lg">
                  <div className="flex items-start gap-4">
                    {member.imageUrl ? (
                      <img 
                        src={member.imageUrl} 
                        alt={member.name}
                        className="w-12 h-12 rounded-full object-cover"
                      />
                    ) : (
                      <div className="w-12 h-12 rounded-full bg-gray-200 flex items-center justify-center">
                        <span className="text-gray-500 font-medium">
                          {member.name.split(' ').map((n: string) => n[0]).join('')}
                        </span>
                      </div>
                    )}
                    <div className="flex-1">
                      <h3 className="font-medium">{member.name}</h3>
                      <p className="text-sm text-blue-600 mb-1">{member.role}</p>
                      <p className="text-sm text-gray-600 mb-2 line-clamp-2">{member.bio}</p>
                      <div className="flex items-center gap-2">
                        <Badge variant={member.isActive ? "default" : "secondary"}>
                          {member.isActive ? "Active" : "Inactive"}
                        </Badge>
                        {member.isActive ? (
                          <Eye className="w-4 h-4 text-green-600" />
                        ) : (
                          <EyeOff className="w-4 h-4 text-gray-400" />
                        )}
                      </div>
                      <div className="flex gap-2 mt-2">
                        {member.linkedinUrl && <Linkedin className="w-4 h-4 text-blue-600" />}
                        {member.githubUrl && <Github className="w-4 h-4 text-gray-800" />}
                        {member.twitterUrl && <Twitter className="w-4 h-4 text-blue-400" />}
                      </div>
                    </div>
                  </div>
                  <div className="flex gap-2 ml-4">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => setEditingItem(member)}
                      data-testid={`button-edit-team-member-${member.id}`}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => deleteMutation.mutate(member.id)}
                      data-testid={`button-delete-team-member-${member.id}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {editingItem && (
          <Card>
            <CardHeader>
              <CardTitle>{isCreating ? "Create Team Member" : "Edit Team Member"}</CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                  <Label htmlFor="name">Name</Label>
                  <Input
                    id="name"
                    value={editingItem.name || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, name: e.target.value })}
                    required
                    data-testid="input-team-member-name"
                  />
                </div>

                <div>
                  <Label htmlFor="role">Role</Label>
                  <Input
                    id="role"
                    value={editingItem.role || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, role: e.target.value })}
                    required
                    data-testid="input-team-member-role"
                  />
                </div>

                <div>
                  <Label htmlFor="bio">Bio</Label>
                  <Textarea
                    id="bio"
                    value={editingItem.bio || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, bio: e.target.value })}
                    rows={3}
                    data-testid="textarea-team-member-bio"
                  />
                </div>

                <div>
                  <Label>Profile Image</Label>
                  <div className="mt-2">
                    <ObjectUploader
                      onGetUploadParameters={handleImageUpload}
                      onComplete={handleImageUploadComplete}
                      maxNumberOfFiles={1}
                      maxFileSize={5 * 1024 * 1024} // 5MB
                    >
                      <span>Upload Profile Image</span>
                    </ObjectUploader>
                    {editingItem.imageUrl && (
                      <div className="mt-2">
                        <img 
                          src={editingItem.imageUrl} 
                          alt="Profile preview" 
                          className="w-24 h-24 rounded-full object-cover border"
                        />
                      </div>
                    )}
                  </div>
                </div>

                <div>
                  <Label htmlFor="expertise">Expertise (comma-separated)</Label>
                  <Input
                    id="expertise"
                    value={Array.isArray(editingItem.expertise) ? editingItem.expertise.join(', ') : editingItem.expertise || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, expertise: e.target.value })}
                    placeholder="React, Node.js, AI/ML, Leadership"
                    data-testid="input-team-member-expertise"
                  />
                </div>

                <div className="grid grid-cols-1 gap-4">
                  <div>
                    <Label htmlFor="linkedinUrl">LinkedIn URL</Label>
                    <Input
                      id="linkedinUrl"
                      type="url"
                      value={editingItem.linkedinUrl || ""}
                      onChange={(e) => setEditingItem({ ...editingItem, linkedinUrl: e.target.value })}
                      placeholder="https://linkedin.com/in/username"
                      data-testid="input-team-member-linkedin"
                    />
                  </div>
                  <div>
                    <Label htmlFor="githubUrl">GitHub URL</Label>
                    <Input
                      id="githubUrl"
                      type="url"
                      value={editingItem.githubUrl || ""}
                      onChange={(e) => setEditingItem({ ...editingItem, githubUrl: e.target.value })}
                      placeholder="https://github.com/username"
                      data-testid="input-team-member-github"
                    />
                  </div>
                  <div>
                    <Label htmlFor="twitterUrl">Twitter URL</Label>
                    <Input
                      id="twitterUrl"
                      type="url"
                      value={editingItem.twitterUrl || ""}
                      onChange={(e) => setEditingItem({ ...editingItem, twitterUrl: e.target.value })}
                      placeholder="https://twitter.com/username"
                      data-testid="input-team-member-twitter"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="sortOrder">Sort Order</Label>
                  <Input
                    id="sortOrder"
                    type="number"
                    value={editingItem.sortOrder || 0}
                    onChange={(e) => setEditingItem({ ...editingItem, sortOrder: parseInt(e.target.value) })}
                    data-testid="input-team-member-sort-order"
                  />
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    id="isActive"
                    checked={editingItem.isActive || false}
                    onCheckedChange={(checked) => setEditingItem({ ...editingItem, isActive: checked })}
                    data-testid="switch-team-member-active"
                  />
                  <Label htmlFor="isActive">Active</Label>
                </div>

                <div className="flex gap-2">
                  <Button type="submit" disabled={createMutation.isPending || updateMutation.isPending} data-testid="button-save-team-member">
                    {isCreating ? "Create Team Member" : "Update Team Member"}
                  </Button>
                  <Button type="button" variant="outline" onClick={() => { setEditingItem(null); setIsCreating(false); }} data-testid="button-cancel-team-member">
                    Cancel
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin/TechnologyManager.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import AdminLayout from "@/components/AdminLayout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Plus, Edit, Trash2, Save, Code } from "lucide-react";

const techCategories = [
  { value: 'backend', label: 'Back-End' },
  { value: 'frontend', label: 'Front-End' },
  { value: 'ai-ml', label: 'AI/ML' },
  { value: 'data', label: 'Data' },
  { value: 'devops', label: 'DevOps' },
];

export default function TechnologyManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingId, setEditingId] = useState<string | null>(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    category: "backend",
    icon: "",
    url: "",
    description: "",
    isActive: true,
    sortOrder: 0
  });

  const { data: technologies, isLoading } = useQuery({
    queryKey: ["/api/admin/technologies"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      await apiRequest("POST", "/api/admin/technologies", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/technologies"] });
      resetForm();
      toast({
        title: "Technology created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error creating technology",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      await apiRequest("PUT", `/api/admin/technologies/${editingId}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/technologies"] });
      resetForm();
      toast({
        title: "Technology updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error updating technology",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/admin/technologies/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/technologies"] });
      toast({
        title: "Technology deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error deleting technology",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      category: "backend",
      icon: "",
      url: "",
      description: "",
      isActive: true,
      sortOrder: 0
    });
    setEditingId(null);
    setShowForm(false);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingId) {
      updateMutation.mutate(formData);
    } else {
      createMutation.mutate(formData);
    }
  };

  const startEdit = (technology: any) => {
    setEditingId(technology.id);
    setFormData({
      name: technology.name || "",
      category: technology.category || "backend",
      icon: technology.icon || "",
      url: technology.url || "",
      description: technology.description || "",
      isActive: technology.isActive ?? true,
      sortOrder: technology.sortOrder || 0
    });
    setShowForm(true);
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className="flex items-center justify-center min-h-96">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pi-blue"></div>
        </div>
      </AdminLayout>
    );
  }

  const groupedTechnologies = techCategories.map(category => ({
    ...category,
    technologies: technologies?.filter((tech: any) => tech.category === category.value) || []
  }));

  return (
    <AdminLayout>
      <div className="space-y-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white" data-testid="text-page-title">
              Technology Management
            </h1>
            <p className="text-pi-gray mt-2" data-testid="text-page-description">
              Manage technology stack and tools
            </p>
          </div>
          <Button onClick={() => setShowForm(!showForm)} data-testid="button-add-technology">
            <Plus className="w-4 h-4 mr-2" />
            Add Technology
          </Button>
        </div>

        {/* Add/Edit Form */}
        {showForm && (
          <Card>
            <CardHeader>
              <CardTitle data-testid="text-form-title">
                {editingId ? "Edit Technology" : "Add New Technology"}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="name">Technology Name *</Label>
                    <Input
                      id="name"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      placeholder="e.g., React, Python, AWS"
                      required
                      data-testid="input-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="category">Category *</Label>
                    <Select value={formData.category} onValueChange={(value) => setFormData({ ...formData, category: value })}>
                      <SelectTrigger data-testid="select-category">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {techCategories.map((category) => (
                          <SelectItem key={category.value} value={category.value}>
                            {category.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="url">Official Website</Label>
                    <Input
                      id="url"
                      type="url"
                      value={formData.url}
                      onChange={(e) => setFormData({ ...formData, url: e.target.value })}
                      placeholder="https://technology.com"
                      data-testid="input-url"
                    />
                  </div>
                  <div>
                    <Label htmlFor="icon">Icon/Logo URL</Label>
                    <Input
                      id="icon"
                      value={formData.icon}
                      onChange={(e) => setFormData({ ...formData, icon: e.target.value })}
                      placeholder="URL to technology logo"
                      data-testid="input-icon"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    rows={3}
                    placeholder="Brief description of the technology"
                    data-testid="textarea-description"
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="isActive"
                      checked={formData.isActive}
                      onCheckedChange={(checked) => setFormData({ ...formData, isActive: !!checked })}
                      data-testid="checkbox-isActive"
                    />
                    <Label htmlFor="isActive">Active</Label>
                  </div>
                  <div>
                    <Label htmlFor="sortOrder">Sort Order</Label>
                    <Input
                      id="sortOrder"
                      type="number"
                      value={formData.sortOrder}
                      onChange={(e) => setFormData({ ...formData, sortOrder: parseInt(e.target.value) || 0 })}
                      data-testid="input-sortOrder"
                    />
                  </div>
                </div>

                <div className="flex space-x-2">
                  <Button 
                    type="submit" 
                    disabled={createMutation.isPending || updateMutation.isPending}
                    data-testid="button-save"
                  >
                    <Save className="w-4 h-4 mr-2" />
                    {editingId ? "Update" : "Create"}
                  </Button>
                  <Button type="button" variant="outline" onClick={resetForm} data-testid="button-cancel">
                    Cancel
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        )}

        {/* Technologies by Category */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {groupedTechnologies.map((category) => (
            <Card key={category.value}>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2" data-testid={`text-category-${category.value}`}>
                  <Code className="w-5 h-5" />
                  <span>{category.label}</span>
                  <span className="text-sm font-normal text-pi-gray">
                    ({category.technologies.length})
                  </span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {category.technologies.length === 0 ? (
                  <p className="text-pi-gray text-sm" data-testid={`text-no-technologies-${category.value}`}>
                    No technologies in this category
                  </p>
                ) : (
                  <div className="space-y-3">
                    {category.technologies.map((tech: any) => (
                      <div 
                        key={tech.id}
                        className="flex items-center justify-between p-3 border border-gray-200 dark:border-gray-700 rounded-lg"
                        data-testid={`technology-${tech.name.toLowerCase().replace(/\s+/g, '-')}`}
                      >
                        <div className="flex items-center space-x-3">
                          {tech.icon && (
                            <img src={tech.icon} alt={tech.name} className="w-6 h-6 object-contain" />
                          )}
                          <div>
                            <h4 className="font-medium text-gray-900 dark:text-white" data-testid={`text-tech-name-${tech.name.toLowerCase().replace(/\s+/g, '-')}`}>
                              {tech.name}
                            </h4>
                            {tech.description && (
                              <p className="text-xs text-pi-gray" data-testid={`text-tech-description-${tech.name.toLowerCase().replace(/\s+/g, '-')}`}>
                                {tech.description}
                              </p>
                            )}
                          </div>
                          {!tech.isActive && (
                            <span className="text-xs px-2 py-1 bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200 rounded">
                              Inactive
                            </span>
                          )}
                        </div>
                        <div className="flex space-x-1">
                          {tech.url && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => window.open(tech.url, '_blank')}
                              data-testid={`button-visit-${tech.name.toLowerCase().replace(/\s+/g, '-')}`}
                            >
                              Visit
                            </Button>
                          )}
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => startEdit(tech)}
                            data-testid={`button-edit-${tech.name.toLowerCase().replace(/\s+/g, '-')}`}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => deleteMutation.mutate(tech.id)}
                            data-testid={`button-delete-${tech.name.toLowerCase().replace(/\s+/g, '-')}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </AdminLayout>
  );
}

client/src/pages/admin/TestimonialManage.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import { Plus, Edit, Trash2, Star, Eye, EyeOff } from "lucide-react";

export default function TestimonialManager() {
  const [editingItem, setEditingItem] = useState<any>(null);
  const [isCreating, setIsCreating] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: testimonials = [] } = useQuery({
    queryKey: ["/api/testimonials"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/testimonials", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/testimonials"] });
      setIsCreating(false);
      setEditingItem(null);
      toast({ title: "Testimonial created successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error creating testimonial", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return apiRequest("PUT", `/api/admin/testimonials/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/testimonials"] });
      setEditingItem(null);
      toast({ title: "Testimonial updated successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error updating testimonial", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/testimonials/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/testimonials"] });
      toast({ title: "Testimonial deleted successfully" });
    },
    onError: (error) => {
      toast({ 
        title: "Error deleting testimonial", 
        description: error.message,
        variant: "destructive" 
      });
    },
  });

  const handleCreate = () => {
    setIsCreating(true);
    setEditingItem({
      content: "",
      authorName: "",
      authorTitle: "",
      authorCompany: "",
      authorImageUrl: "",
      rating: 5,
      sortOrder: 0,
      isActive: true,
      isFeatured: false,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingItem) return;

    if (isCreating) {
      createMutation.mutate(editingItem);
    } else {
      updateMutation.mutate({ id: editingItem.id, data: editingItem });
    }
  };

  const handleImageUpload = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json();
      return { method: "PUT" as const, url: data.uploadURL };
    } catch (error) {
      toast({
        title: "Error getting upload URL",
        description: "Failed to prepare file upload",
        variant: "destructive",
      });
      throw error;
    }
  };

  const handleImageUploadComplete = async (result: any) => {
    if (result.successful && result.successful.length > 0) {
      const uploadedFile = result.successful[0];
      try {
        const response = await apiRequest("PUT", "/api/admin/images", {
          imageURL: uploadedFile.uploadURL,
        });
        const data = await response.json();
        setEditingItem({ ...editingItem, authorImageUrl: data.objectPath });
        toast({ title: "Image uploaded successfully" });
      } catch (error) {
        toast({
          title: "Error processing image",
          description: "Failed to save image",
          variant: "destructive",
        });
      }
    }
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`w-4 h-4 ${
          i < rating ? "text-yellow-400 fill-current" : "text-gray-300"
        }`}
      />
    ));
  };

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Testimonial Manager</h1>
        <p className="text-gray-600">Manage client testimonials and reviews.</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle>Testimonials</CardTitle>
            <Button onClick={handleCreate} data-testid="button-add-testimonial">
              <Plus className="w-4 h-4 mr-2" />
              Add Testimonial
            </Button>
          </CardHeader>
          <CardContent>
            <div className="space-y-4 max-h-96 overflow-y-auto">
              {testimonials.map((testimonial: any) => (
                <div key={testimonial.id} className="flex items-start justify-between p-4 border rounded-lg">
                  <div className="flex items-start gap-4 flex-1">
                    {testimonial.authorImageUrl ? (
                      <img 
                        src={testimonial.authorImageUrl} 
                        alt={testimonial.authorName}
                        className="w-12 h-12 rounded-full object-cover"
                      />
                    ) : (
                      <div className="w-12 h-12 rounded-full bg-gray-200 flex items-center justify-center">
                        <span className="text-gray-500 font-medium">
                          {testimonial.authorName.split(' ').map((n: string) => n[0]).join('')}
                        </span>
                      </div>
                    )}
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h3 className="font-medium">{testimonial.authorName}</h3>
                        {testimonial.isFeatured && <Star className="w-4 h-4 text-yellow-500 fill-current" />}
                      </div>
                      <div className="flex items-center gap-2 mb-2">
                        {renderStars(testimonial.rating)}
                        <span className="text-sm text-gray-500">({testimonial.rating}/5)</span>
                      </div>
                      <p className="text-sm text-blue-600 mb-1">
                        {testimonial.authorTitle}
                        {testimonial.authorCompany && `, ${testimonial.authorCompany}`}
                      </p>
                      <p className="text-sm text-gray-600 mb-2 line-clamp-3">{testimonial.content}</p>
                      <div className="flex items-center gap-2">
                        <Badge variant={testimonial.isActive ? "default" : "secondary"}>
                          {testimonial.isActive ? "Active" : "Inactive"}
                        </Badge>
                        {testimonial.isFeatured && (
                          <Badge variant="outline" className="text-yellow-600 border-yellow-600">
                            Featured
                          </Badge>
                        )}
                        {testimonial.isActive ? (
                          <Eye className="w-4 h-4 text-green-600" />
                        ) : (
                          <EyeOff className="w-4 h-4 text-gray-400" />
                        )}
                      </div>
                    </div>
                  </div>
                  <div className="flex gap-2 ml-4">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => setEditingItem(testimonial)}
                      data-testid={`button-edit-testimonial-${testimonial.id}`}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => deleteMutation.mutate(testimonial.id)}
                      data-testid={`button-delete-testimonial-${testimonial.id}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {editingItem && (
          <Card>
            <CardHeader>
              <CardTitle>{isCreating ? "Create Testimonial" : "Edit Testimonial"}</CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                  <Label htmlFor="content">Testimonial Content</Label>
                  <Textarea
                    id="content"
                    value={editingItem.content || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, content: e.target.value })}
                    required
                    rows={4}
                    placeholder="Share your testimonial content here..."
                    data-testid="textarea-testimonial-content"
                  />
                </div>

                <div>
                  <Label htmlFor="authorName">Author Name</Label>
                  <Input
                    id="authorName"
                    value={editingItem.authorName || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, authorName: e.target.value })}
                    required
                    data-testid="input-testimonial-author-name"
                  />
                </div>

                <div>
                  <Label htmlFor="authorTitle">Author Title</Label>
                  <Input
                    id="authorTitle"
                    value={editingItem.authorTitle || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, authorTitle: e.target.value })}
                    placeholder="CEO, Director of Engineering, etc."
                    data-testid="input-testimonial-author-title"
                  />
                </div>

                <div>
                  <Label htmlFor="authorCompany">Author Company</Label>
                  <Input
                    id="authorCompany"
                    value={editingItem.authorCompany || ""}
                    onChange={(e) => setEditingItem({ ...editingItem, authorCompany: e.target.value })}
                    data-testid="input-testimonial-author-company"
                  />
                </div>

                <div>
                  <Label>Author Profile Image</Label>
                  <div className="mt-2">
                    <ObjectUploader
                      onGetUploadParameters={handleImageUpload}
                      onComplete={handleImageUploadComplete}
                      maxNumberOfFiles={1}
                      maxFileSize={5 * 1024 * 1024} // 5MB
                    >
                      <span>Upload Author Image</span>
                    </ObjectUploader>
                    {editingItem.authorImageUrl && (
                      <div className="mt-2">
                        <img 
                          src={editingItem.authorImageUrl} 
                          alt="Author preview" 
                          className="w-16 h-16 rounded-full object-cover border"
                        />
                      </div>
                    )}
                  </div>
                </div>

                <div>
                  <Label htmlFor="rating">Rating</Label>
                  <div className="flex items-center gap-4 mt-2">
                    <Input
                      id="rating"
                      type="number"
                      min="1"
                      max="5"
                      value={editingItem.rating || 5}
                      onChange={(e) => setEditingItem({ ...editingItem, rating: parseInt(e.target.value) })}
                      className="w-20"
                      data-testid="input-testimonial-rating"
                    />
                    <div className="flex gap-1">
                      {renderStars(editingItem.rating || 5)}
                    </div>
                  </div>
                </div>

                <div>
                  <Label htmlFor="sortOrder">Sort Order</Label>
                  <Input
                    id="sortOrder"
                    type="number"
                    value={editingItem.sortOrder || 0}
                    onChange={(e) => setEditingItem({ ...editingItem, sortOrder: parseInt(e.target.value) })}
                    data-testid="input-testimonial-sort-order"
                  />
                </div>

                <div className="flex items-center space-x-4">
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isFeatured"
                      checked={editingItem.isFeatured || false}
                      onCheckedChange={(checked) => setEditingItem({ ...editingItem, isFeatured: checked })}
                      data-testid="switch-testimonial-featured"
                    />
                    <Label htmlFor="isFeatured">Featured</Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isActive"
                      checked={editingItem.isActive || false}
                      onCheckedChange={(checked) => setEditingItem({ ...editingItem, isActive: checked })}
                      data-testid="switch-testimonial-active"
                    />
                    <Label htmlFor="isActive">Active</Label>
                  </div>
                </div>

                <div className="flex gap-2">
                  <Button type="submit" disabled={createMutation.isPending || updateMutation.isPending} data-testid="button-save-testimonial">
                    {isCreating ? "Create Testimonial" : "Update Testimonial"}
                  </Button>
                  <Button type="button" variant="outline" onClick={() => { setEditingItem(null); setIsCreating(false); }} data-testid="button-cancel-testimonial">
                    Cancel
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

